<script lang="ts">
    import { onDestroy, onMount } from 'svelte';
    import { GrandDataDistributor } from '$lib';
    import { fsmState } from '$lib/stores/state';

    let system_check: SVGGElement;
    let idle: SVGGElement;
    let charging: SVGGElement;
    let active: SVGGElement;
    let operating: SVGGElement;
    let allStates: SVGGElement[];

    const storeManager = GrandDataDistributor.getInstance().stores;
    const mainFsmState = storeManager.getWritable("FSMState");
    const emergencyState = storeManager.getWritable("EmergencyFSMState");

    function turn_on(state:SVGGElement) {
        if (!state || !state.style) return;
        if ($emergencyState.value > 0) {
            state.style.fill = '#D61111'
        } else {
            state.style.fill = '#0EA774'
        }
    }

    function turn_off_all(states:SVGGElement[]) {
        states.forEach(s => {
            if (!s || !s.style) return;
            s.style.fill = 'none'
        });
    }

    let interval: NodeJS.Timeout;

    onMount(() => {
        allStates = [
            system_check,
            idle,
            charging,
            active,
            operating
        ]

        interval = setInterval(() => {
            turn_off_all(allStates);
            // turn_on(allStates[$mainFsmState.value % allStates.length])
            turn_on(allStates[$fsmState]);
        }, 100)
    })

    onDestroy(() => {
        clearInterval(interval)
    });
</script>

